import React, { useState } from "react";
import FontAwesome from "../uiStyle/FontAwesome";
import { Link } from "react-router-dom";
import Swiper from "react-id-swiper";
import moment from "moment";

const TrendingNewsSlider = (props) => {
  const [swiper, setSwiper] = useState(null);
  const { data } = props;

  const goNext = () => {
    if (swiper !== null) {
      swiper.slideNext();
    }
  };

  const goPrev = () => {
    if (swiper !== null) {
      swiper.slidePrev();
    }
  };
  const params = {
    slidesPerView: 2,
    spaceBetween: 10,
    slidesToScroll: 2,
    loop: true,
    navigation: {
      nextEl: ".swiper-button-next",
      prevEl: ".swiper-button-prev",
    },
    breakpoints: {
      1880: {
        slidesPerView: 2,
        spaceBetween: 10,
      },
      1280: {
        slidesPerView: 2,
        spaceBetween: 10,
      },
      1024: {
        slidesPerView: 1,
        spaceBetween: 10,
      },
      768: {
        slidesPerView: 1,
        spaceBetween: 20,
      },
      300: {
        slidesPerView: 1,
        spaceBetween: 20,
      },
    },
  };
  return (
    <div className="carousel_post2_type3 nav_style1">
      <Swiper
        getSwiper={setSwiper}
        className="trancarousel"
        {...params}
        observeParents
        observer
      >
        {data.map((item, i) => (
          <div key={i} className="single_post post_type3">
            <div className="post_img">
              <div className="img_wrap">
                <img
                  src={
                    item.image.includes("http")
                      ? item.image
                      : "data:image/png;base64,"
                  }
                  alt="thumb"
                  width={350}
                  height={200}
                />
              </div>
              <span className="tranding">
                <FontAwesome name="eye" /> {item.viewCount}
              </span>
            </div>
            <div className="single_post_text">
              <div className="meta3">
                <Link
                  to={{
                    pathname: "/search",
                    state: {
                      title: "Danh má»¥c: " + item.category.subCategory,
                      CategoryID: item.category.categoryId,
                    },
                  }}
                >
                  {item.category.subCategory}
                </Link>
                <Link to={`/post-detail/${item.postId}`}>
                  {moment(item.publicTime).format("DD.MM.YYYY")}
                </Link>
              </div>
              <h4>
                <Link to={`/post-detail/${item.postId}`}>
                  {item.title.substring(0, 70) + "..."}
                </Link>
              </h4>
              <div className="space-10" />
              <p className="post-p">{item.subTitle.substring(0, 90) + "..."}</p>
            </div>
          </div>
        ))}
      </Swiper>
      <div className="navBtns">
        <div onClick={goPrev} className="navBtn prevtBtn">
          <FontAwesome name="angle-left" />
        </div>
        <div onClick={goNext} className="navBtn nextBtn">
          <FontAwesome name="angle-right" />
        </div>
      </div>
    </div>
  );
};

export default TrendingNewsSlider;
